'use strict';var React = require('react');
var WeaponActions = require('./actions/weapon_actions');

var Weapon = React.createClass({ displayName: 'Weapon', 

  render: function render() {
    switch (this.props.weapon.type) {
      case 'mis':
        var weapon_class = 'missile';
        break;
      case 'las':
        var weapon_class = 'laser';
        break;
      case 'bal':
        var weapon_class = 'ballistic';
        break;}


    return React.createElement('weapon', { onClick: this._onClick, className: weapon_class }, this.props.weapon.name);}, 


  _onClick: function _onClick() {
    WeaponActions.equip(this);} });




module.exports = Weapon;